import React from 'react';
import './BoxButton.css';

export interface BoxButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  variant?: 'primary' | 'secondary' | 'outline' | 'tertiary';
  size?: 'small' | 'medium' | 'large';
  children: React.ReactNode;
}

export const BoxButton = React.forwardRef<HTMLButtonElement, BoxButtonProps>(
  ({ variant = 'primary', size = 'medium', className = '', children, ...props }, ref) => {
    const classes = [
      'box-button',
      `box-button--${variant}`,
      `box-button--${size}`,
      className
    ].filter(Boolean).join(' ');

    return (
      <button ref={ref} className={classes} {...props}>
        {children}
      </button>
    );
  }
);

BoxButton.displayName = 'BoxButton';

@import '../tokens.css';

.box-button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border: none;
  cursor: pointer;
  font-family: var(--body-default-font-family);
  font-weight: var(--body-default-bold-font-weight);
  letter-spacing: var(--body-default-letter-spacing);
  transition: all var(--animation-duration-2) ease;
  outline: none;
  position: relative;
}

.box-button:focus-visible {
  outline: var(--border-2) solid var(--outline-focus-on-light);
  outline-offset: var(--space-1);
}

.box-button:disabled {
  cursor: not-allowed;
  opacity: 0.5;
}

/* Primary variant */
.box-button--primary {
  background-color: var(--surface-surface-brand);
  color: var(--text-text-on-dark);
  border-radius: var(--radius-half);
}

.box-button--primary:hover:not(:disabled) {
  background-color: var(--surface-surface-brand-hover);
}

.box-button--primary:active:not(:disabled) {
  background-color: var(--surface-surface-brand-pressed);
}

/* Secondary variant */
.box-button--secondary {
  background-color: var(--surface-cta-surface-secondary);
  color: var(--text-text-on-light);
  border: var(--border-1) solid var(--border-cta-border-secondary);
  border-radius: var(--radius-half);
}

.box-button--secondary:hover:not(:disabled) {
  background-color: var(--surface-cta-surface-secondary-hover);
  border-color: var(--border-cta-border-secondary-hover);
}

.box-button--secondary:active:not(:disabled) {
  background-color: var(--surface-cta-surface-secondary-pressed);
  border-color: var(--border-cta-border-secondary-pressed);
}

/* Outline variant */
.box-button--outline {
  background-color: var(--surface-cta-surface-outline);
  color: var(--text-text-on-light);
  border: var(--border-1) solid var(--border-cta-border-outline);
  border-radius: var(--radius-half);
}

.box-button--outline:hover:not(:disabled) {
  background-color: var(--surface-cta-surface-outline-hover);
  border-color: var(--border-cta-border-outline-hover);
}

.box-button--outline:active:not(:disabled) {
  background-color: var(--surface-cta-surface-outline-pressed);
  border-color: var(--border-cta-border-outline-pressed);
}

/* Tertiary variant */
.box-button--tertiary {
  background-color: var(--surface-cta-surface-tertiary);
  color: var(--text-text-on-light);
  border-radius: var(--radius-half);
}

.box-button--tertiary:hover:not(:disabled) {
  background-color: var(--surface-cta-surface-tertiary-hover);
}

.box-button--tertiary:active:not(:disabled) {
  background-color: var(--surface-cta-surface-tertiary-pressed);
}

/* Size variants */
.box-button--small {
  padding: var(--space-2) var(--space-4);
  font-size: var(--caption-default-font-size);
  line-height: var(--caption-default-line-height);
  min-height: var(--size-8);
}

.box-button--medium {
  padding: var(--space-3) var(--space-6);
  font-size: var(--body-default-font-size);
  line-height: var(--body-default-line-height);
  min-height: var(--size-10);
}

.box-button--large {
  padding: var(--space-4) var(--space-8);
  font-size: var(--body-large-font-size);
  line-height: var(--body-large-line-height);
  min-height: var(--size-12);
}

:root {
  /* Surface tokens */
  --surface-surface-secondary: #fbfbfb;
  --surface-surface-brand: #0061d5;
  --surface-surface-brand-hover: #006ae9;
  --surface-surface-brand-pressed: #004eac;
  --surface-cta-surface-secondary: #ffffff;
  --surface-cta-surface-secondary-hover: #f4f4f4;
  --surface-cta-surface-secondary-pressed: #e8e8e8;
  --surface-cta-surface-outline: rgba(0, 0, 0, 0);
  --surface-cta-surface-outline-hover: rgba(0, 0, 0, 0.04);
  --surface-cta-surface-outline-pressed: rgba(0, 0, 0, 0.08);
  --surface-cta-surface-icon: rgba(0, 0, 0, 0);
  --surface-cta-surface-icon-hover: rgba(0, 0, 0, 0.04);
  --surface-cta-surface-icon-pressed: rgba(0, 0, 0, 0.08);
  --surface-cta-surface-tertiary: #ffffff;
  --surface-cta-surface-tertiary-hover: #f4f4f4;
  --surface-cta-surface-tertiary-pressed: #e8e8e8;

  /* Border tokens */
  --border-cta-border-secondary: #bcbcbc;
  --border-cta-border-secondary-pressed: #bcbcbc;
  --border-cta-border-secondary-hover: #bcbcbc;
  --border-cta-border-outline: #000000;
  --border-cta-border-outline-hover: #000000;
  --border-cta-border-outline-pressed: #000000;
  --border-1: 0.0625rem;
  --border-2: 0.125rem;

  /* Text tokens */
  --text-text-on-light: #222222;
  --text-text-on-dark: #ffffff;

  /* Outline tokens */
  --outline-focus-on-light: #2486fc;

  /* Typography tokens */
  --body-large-font-family: Lato, -apple-system, BlinkMacSystemFont, 'San Francisco', 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
  --body-large-font-weight: 400;
  --body-large-line-height: 1.5rem;
  --body-large-font-size: 1rem;
  --body-large-letter-spacing: 0.01875rem;

  --body-default-bold-font-weight: 700;
  --body-default-font-family: Lato, -apple-system, BlinkMacSystemFont, 'San Francisco', 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
  --body-default-font-weight: 400;
  --body-default-line-height: 1.25rem;
  --body-default-font-size: 0.875rem;
  --body-default-letter-spacing: 0.01875rem;

  --caption-default-font-family: Lato, -apple-system, BlinkMacSystemFont, 'San Francisco', 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
  --caption-default-font-weight: 400;
  --caption-default-line-height: 0.875rem;
  --caption-default-font-size: 0.75rem;
  --caption-default-letter-spacing: 0.01875rem;

  /* Radius tokens */
  --radius-3: 0.5rem;
  --radius-half: 2rem;

  /* Space tokens */
  --space-1: 0.25rem;
  --space-2: 0.5rem;
  --space-3: 0.75rem;
  --space-4: 1rem;
  --space-6: 1.5rem;
  --space-8: 2rem;

  /* Size tokens */
  --size-3: 0.75rem;
  --size-4: 1rem;
  --size-5: 1.25rem;
  --size-8: 2rem;
  --size-10: 2.5rem;
  --size-12: 3rem;

  /* Animation tokens */
  --animation-duration-2: 200ms;
}
