import React from 'react';
import './BoxIconButton.css';

export interface BoxIconButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  /**
   * The icon to display inside the button
   */
  children: React.ReactNode;
  /**
   * Size variant of the icon button
   */
  size?: 'small' | 'medium' | 'large';
  /**
   * Visual variant of the icon button
   */
  variant?: 'default' | 'subtle' | 'ghost';
}

export const BoxIconButton = React.forwardRef<HTMLButtonElement, BoxIconButtonProps>(
  ({ children, size = 'medium', variant = 'default', className = '', ...props }, ref) => {
    const classes = [
      'box-icon-button',
      `box-icon-button--${variant}`,
      `box-icon-button--${size}`,
      className
    ].filter(Boolean).join(' ');

    return (
      <button ref={ref} className={classes} type="button" {...props}>
        {children}
      </button>
    );
  }
);

BoxIconButton.displayName = 'BoxIconButton';

@import '../tokens.css';

.box-icon-button {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border: none;
  cursor: pointer;
  font-family: var(--body-default-font-family);
  transition: all var(--animation-duration-2) ease;
  outline: none;
  position: relative;
  background-color: var(--surface-surface-secondary);
  color: var(--text-text-on-light);
  border-radius: var(--radius-3);
  flex-shrink: 0;
}

/* Focus state with blue border */
.box-icon-button:focus-visible {
  outline: var(--border-2) solid var(--outline-focus-on-light);
  outline-offset: 0;
  box-shadow: 0 0 0 var(--border-2) var(--outline-focus-on-light);
}

/* Pressed state */
.box-icon-button:active:not(:disabled) {
  background-color: var(--surface-cta-surface-icon-pressed);
  transform: scale(0.98);
}

/* Hover state */
.box-icon-button:hover:not(:disabled) {
  background-color: var(--surface-cta-surface-icon-hover);
}

/* Disabled state */
.box-icon-button:disabled {
  cursor: not-allowed;
  opacity: 0.4;
  background-color: var(--surface-surface-secondary);
  color: var(--text-text-on-light);
}

/* Default variant */
.box-icon-button--default {
  background-color: var(--surface-surface-secondary);
}

.box-icon-button--default:hover:not(:disabled) {
  background-color: var(--surface-cta-surface-icon-hover);
}

.box-icon-button--default:active:not(:disabled) {
  background-color: var(--surface-cta-surface-icon-pressed);
}

/* Subtle variant */
.box-icon-button--subtle {
  background-color: var(--surface-cta-surface-icon);
}

.box-icon-button--subtle:hover:not(:disabled) {
  background-color: var(--surface-cta-surface-icon-hover);
}

.box-icon-button--subtle:active:not(:disabled) {
  background-color: var(--surface-cta-surface-icon-pressed);
}

/* Ghost variant */
.box-icon-button--ghost {
  background-color: transparent;
}

.box-icon-button--ghost:hover:not(:disabled) {
  background-color: var(--surface-cta-surface-icon-hover);
}

.box-icon-button--ghost:active:not(:disabled) {
  background-color: var(--surface-cta-surface-icon-pressed);
}

/* Size variants */
.box-icon-button--small {
  width: var(--size-8);
  height: var(--size-8);
  font-size: var(--size-3);
  padding: var(--space-2);
}

.box-icon-button--small svg {
  width: var(--size-3);
  height: var(--size-3);
}

.box-icon-button--medium {
  width: var(--size-10);
  height: var(--size-10);
  font-size: var(--size-4);
  padding: var(--space-3);
}

.box-icon-button--medium svg {
  width: var(--size-4);
  height: var(--size-4);
}

.box-icon-button--large {
  width: var(--size-12);
  height: var(--size-12);
  font-size: var(--size-5);
  padding: var(--space-4);
}

.box-icon-button--large svg {
  width: var(--size-5);
  height: var(--size-5);
}

:root {
  /* Surface tokens */
  --surface-surface-secondary: #fbfbfb;
  --surface-surface-brand: #0061d5;
  --surface-surface-brand-hover: #006ae9;
  --surface-surface-brand-pressed: #004eac;
  --surface-cta-surface-secondary: #ffffff;
  --surface-cta-surface-secondary-hover: #f4f4f4;
  --surface-cta-surface-secondary-pressed: #e8e8e8;
  --surface-cta-surface-outline: rgba(0, 0, 0, 0);
  --surface-cta-surface-outline-hover: rgba(0, 0, 0, 0.04);
  --surface-cta-surface-outline-pressed: rgba(0, 0, 0, 0.08);
  --surface-cta-surface-icon: rgba(0, 0, 0, 0);
  --surface-cta-surface-icon-hover: rgba(0, 0, 0, 0.04);
  --surface-cta-surface-icon-pressed: rgba(0, 0, 0, 0.08);
  --surface-cta-surface-tertiary: #ffffff;
  --surface-cta-surface-tertiary-hover: #f4f4f4;
  --surface-cta-surface-tertiary-pressed: #e8e8e8;

  /* Border tokens */
  --border-cta-border-secondary: #bcbcbc;
  --border-cta-border-secondary-pressed: #bcbcbc;
  --border-cta-border-secondary-hover: #bcbcbc;
  --border-cta-border-outline: #000000;
  --border-cta-border-outline-hover: #000000;
  --border-cta-border-outline-pressed: #000000;
  --border-1: 0.0625rem;
  --border-2: 0.125rem;

  /* Text tokens */
  --text-text-on-light: #222222;
  --text-text-on-dark: #ffffff;

  /* Outline tokens */
  --outline-focus-on-light: #2486fc;

  /* Typography tokens */
  --body-large-font-family: Lato, -apple-system, BlinkMacSystemFont, 'San Francisco', 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
  --body-large-font-weight: 400;
  --body-large-line-height: 1.5rem;
  --body-large-font-size: 1rem;
  --body-large-letter-spacing: 0.01875rem;

  --body-default-bold-font-weight: 700;
  --body-default-font-family: Lato, -apple-system, BlinkMacSystemFont, 'San Francisco', 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
  --body-default-font-weight: 400;
  --body-default-line-height: 1.25rem;
  --body-default-font-size: 0.875rem;
  --body-default-letter-spacing: 0.01875rem;

  --caption-default-font-family: Lato, -apple-system, BlinkMacSystemFont, 'San Francisco', 'Segoe UI', Roboto, 'Helvetica Neue', sans-serif;
  --caption-default-font-weight: 400;
  --caption-default-line-height: 0.875rem;
  --caption-default-font-size: 0.75rem;
  --caption-default-letter-spacing: 0.01875rem;

  /* Radius tokens */
  --radius-3: 0.5rem;
  --radius-half: 2rem;

  /* Space tokens */
  --space-1: 0.25rem;
  --space-2: 0.5rem;
  --space-3: 0.75rem;
  --space-4: 1rem;
  --space-6: 1.5rem;
  --space-8: 2rem;

  /* Size tokens */
  --size-3: 0.75rem;
  --size-4: 1rem;
  --size-5: 1.25rem;
  --size-8: 2rem;
  --size-10: 2.5rem;
  --size-12: 3rem;

  /* Animation tokens */
  --animation-duration-2: 200ms;
}
